import React, {ReactNode} from 'react';
import './style.scss';

interface MyInputProps {
    children?: string,
    className?: ReactNode,
}

const MyInput = React.forwardRef<HTMLInputElement, MyInputProps>((
    {
        children,
        className,
        ...props
    }, ref) => {

    return (
        <input
            type='text'
            ref={ref}
            className={"MyInput" + (className ? ` ${className}` : '')}
            {...props}
        >
            {children ?? <></>}
        </input>
    );
});

export default MyInput;
