export interface ItemType {
    id: number,
    title: string,
    quantity: number,
    distance: number,
    date: string
}

export enum ItemsActionTypes {
    FETCH_ITEMS = "FETCH_ITEMS",
    SET_FILTERED_ITEMS = "SET_ITEMS_TO_SHOW"
}

export interface FetchItemsAction {
    type: ItemsActionTypes.FETCH_ITEMS,
    payload: ItemType[]
}

export interface SetFilteredItemsAction {
    type: ItemsActionTypes.SET_FILTERED_ITEMS,
    payload: ItemType[]
}

export type ItemsAction = FetchItemsAction | SetFilteredItemsAction;

export interface ItemsState {
    allItems: ItemType[],
    filteredItems: ItemType[]
    sortedItems: ItemType[]
    currentPage: number
}
